/////////////////////////////
//                         //
//    Public attributes    //
//                         //
/////////////////////////////

/**
 * The prefix to use for the CSS classes.
 */
declare const CSS_PREFIX: string;

/**
 * The enter/return key code.
 */
declare const ENTER_KEY_CODE: number;

/**
 * The space key code.
 */
declare const SPACE_KEY_CODE: number;

/**
 * The escape key code.
 *
 * @type {number}
 * @constant
 */
declare const ESCAPE_KEY_CODE: number;

/**
 * The left key code.
 */
declare const LEFT_KEY_CODE: number;

/**
 * The right key code.
 */
declare const RIGHT_KEY_CODE: number;

/**
 * The down arrow key code.
 */
declare const DOWN_KEY_CODE: number;

/**
 * The up arrow key code.
 */
declare const UP_KEY_CODE: number;

/**
 * The TAB key code.
 */
declare const TAB_KEY_CODE: number;

/**
 * The BACKSPACE key code.
 */
declare const BACKSPACE_KEY_CODE: number;

/////////////////////////////

export {
    BACKSPACE_KEY_CODE,
    CSS_PREFIX,
    ENTER_KEY_CODE,
    SPACE_KEY_CODE,
    ESCAPE_KEY_CODE,
    LEFT_KEY_CODE,
    RIGHT_KEY_CODE,
    UP_KEY_CODE,
    DOWN_KEY_CODE,
    TAB_KEY_CODE,
};
